import React, { useState } from 'react'
import './Top.css'
import info_icon from '../../assets/info _blue.png'
import cloud_ai from '../../assets/cloud_ai2.jpg'

const Top = () => {

  const [showtooltip,setTooltip] = useState(false)

  const handleMouseEnter = ()=>{
    setTooltip(true)
  }

  return (
    <div className='top'>
        <div className='top-bar'>
            <div className='cloud'>
              <img id='cloud-image' src={cloud_ai} alt='' />
              <h1>Cloud AI</h1>
            </div>
            <img onMouseEnter={handleMouseEnter} id='info' src={info_icon} alt=''/>
            {
              showtooltip &&
              <div></div>
            }
        </div>
    </div>
  )
}

export default Top
